$('.order-left-delivery__block').each(function (index) {
    $(this).on('click', () => {
        if ($('.order-left-delivery-container').eq(index).hasClass('active')) {
            $('.order-left-delivery_container').slideUp()
            $('.order-left-delivery-container').removeClass('active')
            $('.order-left-delivery-container').removeClass('active-border');
        } else {
            $('.order-left-delivery_container').slideUp()
            $('.order-left-delivery-container').removeClass('active')
            $('.order-left-delivery-container').removeClass('active-border');

            $('.order-left-delivery_container').eq(index).slideDown()
            $('.order-left-delivery-container').eq(index).addClass('active')
            $('.order-left-delivery-container').eq(index).addClass('active-border');
        }
    })
})

