$(document).ready(function () {
    $('.header-bottom-left').hover(
        function () {
            if ($(window).width() > 999) {
            $('.background').addClass('backgroundOpen');
            $('.catalog').addClass('hovered');
            }
        },
        function () {
            if ($(window).width() > 999) {
            $('.list').removeClass('hovered');
            $('.background').removeClass('backgroundOpen');
            $('.catalog').removeClass('hovered');
            }
        }
    );
});

$('.header-bottom-left-btn').on('click', function()  {
    if ($(window).width() <= 999) {
    $('.background').toggleClass('backgroundOpen');
    $('.catalog').toggleClass('hovered');
    $('.list').removeClass('hovered');
    $('.header-bottom-left-btn').toggleClass('background-btn');
    $('.header-bottom-left-btn-catalog').toggleClass('color-btn');
    $('.header-bottom-left-btn-img-color').toggleClass('color-btn-img');
    $('.header-bottom-left-btn-arrow-color').toggleClass('color-arrow-img');
    $('.header-bottom-left-btn-arrow').toggleClass('rotate-arrow');
    }
})

$('.background').on('click', function()  {
    if ($(window).width() <= 999) {
        $('.background').removeClass('backgroundOpen');
        $('.catalog').removeClass('hovered');
        $('.list').removeClass('hovered');
        $('.header-bottom-left-btn').removeClass('background-btn');
        $('.header-bottom-left-btn-catalog').removeClass('color-btn');
        $('.header-bottom-left-btn-img-color').removeClass('color-btn-img');
        $('.header-bottom-left-btn-arrow-color').removeClass('color-arrow-img');
        $('.header-bottom-left-btn-arrow').removeClass('rotate-arrow');
    }
})

