$('.goods-height-block').each(function (index) {
    $(this).on('click', () => {
        if ($('.goods-container-none-height').eq(index).hasClass('active')) {
            $('.goods-height-hover').removeClass('open');
            $('.svg-rotate').removeClass('rotate');
            $('.goods-container-none-height').removeClass('active')
        } else {
            $('.goods-height-hover').removeClass('open');
            $('.svg-rotate').removeClass('rotate');
            $('.goods-container-none-height').removeClass('active')

            $('.goods-height-hover').eq(index).addClass('open');
            $('.svg-rotate').eq(index).addClass('rotate');
            $('.goods-container-none-height').eq(index).addClass('active')
        }
    })
})

