document.addEventListener('DOMContentLoaded', function () {

    var mainSwiper = new Swiper(".general-slider", {
        breakpoints: {
            320: {
            slidesPerView: 1,
            spaceBetween: 0
            }
        },

        navigation: {
            nextEl: ".general-btn-next",
            prevEl: ".general-btn-prev",
        },
    });

    var miniSwiper = new Swiper(".general-slider-mini", {
        breakpoints: {
            320: {
            slidesPerView: 5,
            spaceBetween: 10
            }
        },

        on: {
            slideChange: function () {
                var activeIndex = this.activeIndex;
                var miniSlides = document.querySelectorAll('.general-slider-mini .swiper-slide');
                miniSlides.forEach(function (slide, index) {
                    slide.classList.remove('active');
                });
                miniSlides[activeIndex].classList.add('active');
            }
        }

    }); 

        mainSwiper.controller.control = miniSwiper;
        miniSwiper.controller.control = mainSwiper;

        var miniSlides = document.querySelectorAll('.general-slider-mini .swiper-slide');
            miniSlides.forEach(function(slide, index) {
            slide.addEventListener('click', function() {
                mainSwiper.slideTo(index);
            });
        });

});

