$('.filter-block-top-arrow').each(function (index) {
    $(this).on('click', () => {
        if ($('.filter-block').eq(index).hasClass('active')) {
            $('.filter-block-bottom').eq(index).slideUp()
            $('.filter-block').eq(index).removeClass('active')
        } else {
            $('.filter-block-bottom').eq(index).slideDown()
            $('.filter-block').eq(index).addClass('active')
        }
    })
})

