var slider = document.getElementById('slider');
var valueDisplay1 = document.getElementById('valueDisplay1');
var valueDisplay2 = document.getElementById('valueDisplay2');

    noUiSlider.create(slider, {
        start: [1000,7000],
        range: {
            'min': 0,
            'max': 10000
        },
        connect: true,
    });

    slider.noUiSlider.on('update', function (values, handle) {
        // Отримуємо поточне значення повзунка
        var value = Math.round(values[handle]);
    
        // Відображаємо значення у відповідному блоці
        if (handle === 0) {
            valueDisplay1.innerHTML = value;
        } else {
            valueDisplay2.innerHTML = value;
        }
    });


